    						if(pbf.iRid_dest_ra==0)this.xm3f.DrawTextEx(this.bfs[j].iX,this.bfs[j].iY,pbf.sStr,pbf.iBw_color_alpha,pbf.iBy_size_y2_raty,101,1,1,0,-1,-1);
