    	{
    		var pc=this.pc_mission.pca.phead;
    		var pc_id;
    		var pn;
    		var pw;
    		while(pc!=null)
    		{
    			pc_id=pc.FindClass("任务id列表");
    			if(pc_id!=null)
    			{
    				pn=pc_id.pca.phead;
    				while(pn!=null)
    				{
    					if(mp==pn.iNumber)
    					{
    						pw=pc.FindWord("是否可放弃");
    						if(pw!=null)
    						{
    							if(pw.pword=="是")return true;
    						}
    						return false;
    					}
    					pn=pn.pdown;
    				}
    			}
    			pc=pc.pdown;
    		}
    		return false;
    	}
