    		if(PublicChat_SmallFrame.gi().bOpen)this.iY=GmConfig$1.SCRH-PublicChat_SmallFrame.gi().iModifyH-this.iH/2-70;
    		else this.iY=GmConfig$1.SCRH-this.iH/2;
